RegisterCommand('npccuff', function() local ped=PlayerPedId(); local h,n=FindFirstPed(); local s=true; local p=GetEntityCoords(ped); local d=2.0; local f=0; repeat local pos=GetEntityCoords(n); local m=#(pos-p); if not IsPedAPlayer(n) and m<d and not IsPedDeadOrDying(n) then f=n; d=m end s,n=FindNextPed(h) until not s EndFindPed(h); if f~=0 then SetEnableHandcuffs(f,true); TaskPlayAnim(f,'mp_arresting','idle',8.0,-8,-1,49,0,false,false,false); TriggerEvent('chat:addMessage',{args={'^2Cuff','Cuffed NPC.'}}) else TriggerEvent('chat:addMessage',{args={'^1Cuff','No NPC close.'}}) end end)
